import * as types from './../constant/ActionTypes.js';

let initialDefault = []


const itemSelected = (state = initialDefault,action) => {
	switch (action.type) {
		case types.UPDATE_PRODUCT:

			return action.product
		default:
			return state
	}
	
}
export default itemSelected;
