import * as types from './../constants/ActionTypes.js';

let initialDefault = 
{	id:'',
	name:' ',
	status:1
}


const itemSelected = (state = initialDefault,action) => {
	switch (action.type) {
		case types.SELECTED_ITEM:	
			return action.item
  		case types.UNSELECT_ITEM: 
  			return {id:'',name:' ',status:1};
		default:
			return state
	}
	
}
export default itemSelected;
