import * as types from './../constant/ActionTypes';
import callAPI from './../libs/apiCaller';
export const actFetchProductsRequest = () => {
	return (dispatch) =>{
			return callAPI('products', 'GET', null).then(res=>{
				dispatch(actFetchProducts(res.data))
			})
		}

}
export const actFetchProducts = (products)=>{
	return {
		type:types.FETCH_PRODUCTS,
		products
	}
}
export const actAddProductRequest = (product) =>{
	return (dispatch) =>{
		return callAPI('products', 'POST', product).then(res=>{
			dispatch(actAddProduct(res.data));

		})
	}
	}
export const actAddProduct = (product) => {
	return {
		type : types.ADD_PRODUCT,
		product
	}
}
export const actDeteleProductRequest = (id) => {
	return (dispatch) =>{
		return callAPI(`products/${id}`, 'DELETE', null).then(res=>{
			dispatch(actDeteleProduct(id))
		})
	}
}
export const actDeteleProduct = (id) => {
	return {
		type : types.DELETE_PRODUCT,
		id
	}
}

export const acUpdateProductRequest = (id) => {
	return (dispatch) =>{
		return callAPI(`products/${id}`, 'PUT', null).then(res=>{
			dispatch(actUpdateProduct(id))
		})
	}
}
export const actUpdateProduct = (id) => {
	return {
		type : types.UPDATE_PRODUCT,
		id
	}
}

export const actSelectedItem = (product) => {
	return {
		type : types.UPDATE_PRODUCT,
		product
	}
}

export const actUnSelectItem = () => {
	return {
		type : types.UNSELECT_PRODUCT,
	}
}
