import React, {Component} from 'react';
import { connect } from 'react-redux';
import ProductList from './../components/ProductList';
import ProductItem from './../components/ProductItem';
import callAPI from './../libs/apiCaller';
import {actFetchProducts} from './../actions/index';
const axios = require('axios').default;
class ProductListPage extends Component {
  constructor(props){
    super(props);
    this.state={
      products:[]
    }
  }
  componentDidMount(){
  callAPI('products','GET',null).then(res=>{
      this.setState({
        products:res.data
      })
    }
    ).catch(err=>{
      console.log(err)
    });
  }

render(){
let {products} = this.state;

 return (
 <div className="container">
    <div className="col-12 col-md-12">
            <button type="button" className="btn btn-primary mb-2">
              Thêm Sản Phẩm 
            </button>
            <ProductList>
            {this.showProducts(products)}
            </ProductList>
        </div>
</div> 
 );

}
showProducts(products){
  let xhml = null; 
if (products !== null && products.length > 0){
xhml = products.map((product,index) => {
  return <ProductItem key={index} index={index} product={product}/>
})
}
return xhml
}
}
const mapDispatchToProps = (dispatch,ownProps)=>{
  return {
   fetchAllPropducts: (products)=>{
     dispatch(actFetchProducts(products))
   }
  }
}
const mapStateToProps = (state) =>{
  return {
    products: state.products
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(ProductListPage);
