import React, {Component} from 'react'
 class ProductItem extends Component {
  onDelete(id) {
console.log(id)
  }

render(){
  let {product,index} = this.props;
  let statusName = (product.status)?'Còn Hàng': 'Hết Hàng rồi nha ^^ ';
  let statusClass = (product.status)? 'success':'warning'; 
  return (
          <tr>
            <td>{index+1}</td>
  <td>{product.id}</td>
  <td>{product.name}</td>
  <td>{product.price}</td>
            <td>
  <span className={`badge badge-${statusClass}`}>{statusName}</span>
            </td>
            <td>
              <button onClick={()=>this.onDelete(product.id)}type="button" className="btn btn-primary mr-3">Sửa</button>
              <button type="button" className="btn btn-danger">Xoá</button>
            </td>
          </tr>
  );
}
}

export default ProductItem;
